import React, { useEffect, useState } from "react";
import axiosInstance from "../services/axiosInstance";

function Home() {
    const [posts, setPosts] = useState([]);
    const [showPopup, setShowPopup] = useState(false); // Estado para controlar o popup
    const [newPost, setNewPost] = useState({ texto: "" }); // Estado para nova postagem
    const token = sessionStorage.getItem("token");

    useEffect(() => {
        if (!token) {
            alert("Acesso negado. Faça login.");
            window.location.href = "/";
            return;
        }

        axiosInstance.get("http://localhost:3001/posts", {
            headers: { Authorization: `Bearer ${token}` }
        })
        .then(response => {
            setPosts(response.data);
        })
        .catch(error => {
            console.error("Erro ao carregar postagens:", error);
            alert("Token inválido ou expirado.");
            window.location.href = "/";
        });
    }, [token]);

    const handleCreatePost = () => {
        axiosInstance.post("http://localhost:3001/posts", newPost, {
            headers: { Authorization: `Bearer ${token}` }
        })
        .then(response => {
            setPosts([...posts, response.data]); // Adiciona a nova postagem à lista
            setShowPopup(false); // Fecha o popup
            setNewPost({ texto: "" }); // Reseta o estado da nova postagem
        })
        .catch(error => {
            console.error("Erro ao criar postagem:", error);
            alert("Erro ao criar postagem.");
        });
    };

    return (
        <div className="home-container">
            <button onClick={() => setShowPopup(true)}>Novo Post</button> {/* Botão para abrir o popup */}
            <h2>Lista de Usuários</h2>
			<button>
				<Link id="Nuser" to="/users/new">Novo Usuário</Link>
			</button>
			<ul>
				{users.map(user => (
					<li key={user.id}>
						{user.fullName} ({user.role})
						<Link to={`/users/edit/${user.id}`}>Editar</Link>
						<button onClick={() => handleDelete(user.id)}>Deletar</button>
					</li>
				))}
			</ul>
        </div>
    );
}

export default Home;
