import React from 'react';

function UserHeader({ user }) {
  return (
    <div className="user-header">

      <img 
        src={`http://localhost:3001/uploads/${user.photo}`} 
        alt="Foto" 
        className="user-photo" 
    />

      <div>
        <h4>{user.fullName}</h4>
        <p>{user.role}</p>
      </div>
    </div>
  );
}

function CloseSidebar() {
  const sidebar = document.querySelector('sidebar'); 
  const paragrafo = sidebar.querySelectorAll('p');
  const linha = sidebar.querySelectorAll('li');
  const ancora = sidebar.querySelectorAll('a');

    if (sidebar.style.display == 'flex') {
      sidebar.style.display = 'none'
      sidebar.style.transition = '0.5s'
      paragrafo.forEach(p => {
        p.style.display = 'none';
        p.style.transition = '0.5s'
      });
      linha.forEach( li => {
        li.style.width = 'auto';
        li.style.margin = 'auto'
      });
      ancora.forEach( a => {
        a.style.width = 'auto';
      });

      document.querySelector('main').style.marginLeft = '0px';
    } else if (window.innerWidth > 1060) {
      sidebar.style.display = 'flex'
      sidebar.style.transition = '0.5s'
      paragrafo.forEach(p => {
        p.style.display = 'block';
        p.style.transition = '0.5s'
      });
      linha.forEach( li => {
        li.style.width = '100%';
        li.style.margin = '1vh 1vw';
      });
      ancora.forEach( a => {
        a.style.width = '100%';
      });

      document.querySelector('main').style.marginLeft = '15.64%';
    }
}
export default UserHeader;
